import { Component } from 'react';
import './App.css';
import Todos from './components/Todos';

class App extends Component{
  state = {
    todos: [
        {
        id: 1,
        title: 'Take out trash',
        completed: false
      },
      {
        id: 2,
        title: 'Applications to do',
        completed: false
      },
      {
        id: 3,
        title: 'Interview at 6:30',
        completed: true
      }
    ]
  }

  // toggle complete 
  markedComplete = (id) => {
    this.setState({ todos: this.state.todos.map(todo => {
      if(todo.id === id){
        todo.completed = !todo.completed;
      }
      return todo;
    }) }); 
}
  render(){
    return (
      <div className="App">
        <Todos todos={this.state.todos} markedComplete={this.markedComplete} 
        delTodo={this.delTodo}/>
      </div>
    );  
  }
}

export default App;
